CREATE TABLE `taracot_blog_comments` (  `id` int(11) NOT NULL auto_increment,  `post_id` int(11) NOT NULL,  `deleted` tinyint(1) default '0',  `cusername` varchar(255) NOT NULL,  `ctext` text,  `cdate` int(11) default NULL,  `chash` varchar(32) default NULL,  `left_key` int(10) NOT NULL,  `right_key` int(10) NOT NULL,  `level` int(10) NOT NULL,  `ipaddr` varchar(45) default NULL,  PRIMARY KEY  (`id`),  KEY `left_key` (`left_key`,`right_key`,`level`)) ENGINE=MyISAM;
CREATE TABLE `taracot_blog_posts` (  `id` int(11) NOT NULL auto_increment,  `plang` varchar(2) default NULL,  `pusername` varchar(255) NOT NULL,  `phub` varchar(20) default NULL,  `pstate` tinyint(3) default '1',  `ptitle` varchar(255) NOT NULL,  `ptags` varchar(255) default NULL,  `pdate` int(11) default NULL,  `ptext` text,  `ptext_html_cut` text,  `pcut` tinyint(1) default '0',  `ptext_html` text,  `pviews` int(11) default '0',  `pcomments` int(11) default '0',  `ipaddr` varchar(45) default NULL,  `mod_require` tinyint(1) default '0',  `deleted` tinyint(1) default '0',  `comments_allowed` tinyint(1) default '1',  `lastchanged` int(11) default NULL,  UNIQUE KEY `id` (`id`),  FULLTEXT KEY `ptags` (`ptags`)) ENGINE=MyISAM;
CREATE TABLE `taracot_firewall` (  `id` int(11) NOT NULL auto_increment,  `ipaddr` varchar(45) default NULL,  `status` tinyint(4) NOT NULL default '0',  `lastchanged` int(11) default NULL,  UNIQUE KEY `id` (`id`),  FULLTEXT KEY `ftdata` (`ipaddr`)) ENGINE=MyISAM;
CREATE TABLE `taracot_pages` (  `id` int(11) NOT NULL auto_increment,  `pagetitle` varchar(255) NOT NULL,  `keywords` varchar(255) default NULL,  `description` varchar(255) default NULL,  `content` text,  `status` int(11) NOT NULL,  `filename` varchar(255) NOT NULL,  `lang` varchar(5) default 'en',  `layout` varchar(40) NOT NULL default 'taracot',  `lastchanged` int(11) default '0',  UNIQUE KEY `id` (`id`),  FULLTEXT KEY `filter` (`pagetitle`,`filename`)) ENGINE=MyISAM;
INSERT INTO `taracot_pages` VALUES (1, 'Home', 'Page keywords', 'Page description', '<p>\nHello! As you can read this, everything seems to be working fine.</p>\n', 1, '/', 'en', 'taracot', 1358176801);
CREATE TABLE `taracot_settings` (  `id` int(11) NOT NULL auto_increment,  `s_name` varchar(255) NOT NULL,  `s_value` mediumtext,  `s_value_html` text,  `lang` varchar(5) NOT NULL default 'en',  `lastchanged` int(11) default '0',  UNIQUE KEY `id` (`id`),  FULLTEXT KEY `s_name` (`s_name`)) ENGINE=MyISAM;
INSERT INTO `taracot_settings` VALUES (1, 'site_title', 'Taracot CMS', '', 'en', 1358181002);
