INSERT INTO `taracot_settings` VALUES (5, 'blog_mode', 'moderate', '<p>Blog mode can be set as:</p>\n\n<ul style="list-style-type:square">\n	<li>public (everyone can create new posts, no moderation)</li>\n	<li>moderate (new posts are created with &quot;mod_require&quot; flag)</li>\n	<li>private (only the users with special permissions are allowed to make new posts)</li>\n</ul>\n', '', 1376033601);
CREATE TABLE `taracot_users` (  `id` int(11) NOT NULL auto_increment,  `username` varchar(100) NOT NULL,  `password` varchar(32) default NULL,  `password_unset` tinyint(1) NOT NULL default '0',  `realname` varchar(100) NOT NULL,  `email` varchar(80) NOT NULL,  `phone` varchar(40) default NULL,  `groups` varchar(255) default NULL,  `status` tinyint(4) NOT NULL default '0',  `verification` varchar(36) default NULL,  `regdate` int(11) default NULL,  `last_lang` varchar(2) default NULL,  `banned` int(11) default '0',  `lastchanged` int(11) default NULL,  UNIQUE KEY `id` (`id`),  FULLTEXT KEY `ftdata` (`username`,`realname`,`email`)) ENGINE=MyISAM;